INSERT INTO "PUBLIC"."YOGA_SERVICE" (DESCRIPTION, IMG_NAME, NAME, PRICE, SHORT_DESCRIPTION)
VALUES ('Основною метою хатха-йоги є досягнення гармонії між тілом і розумом шляхом очищення і зміцнення тіла, розвитку внутрішньої концентрації та спокою. Це досягається через різноманітні фізичні та дихальні вправи, які допомагають підготувати тіло і розум до більш високих стадій медитації. Практики хатха-йоги можуть включати асани (пози), пранаями (дихальні техніки), мудри (ритуальні жести або пози рук), бандхи (енергетичні затиснення), а також широкий спектр технік релаксації та концентрації.', 'yoga1.png', 'ХАТХА-ЙОГА', '100 грн.', 'Занурення у Глибини Тіла та Душі:'),
       ('Віньяса-йога є однією з форм йоги, яка акцентується на координації руху та дихання під час виконання поз або асан. Це досить динамічна і енергійна практика, яка вимагає від практикуючого рівноваги, гнучкості та уваги.Основна ідея Віньяса-йоги полягає в тому, щоб з''єднати дихання з рухом, формуючи плавний та неперервний потік рухів. Кожен рух у Віньяса-йозі поєднується з конкретним вдихом або видахом, що допомагає синхронізувати тіло, розум і дихання.Ця практика також зазвичай включає в себе серії поз, які виконуються у певному порядку. Цей порядок може бути заздалегідь встановлений або адаптований відповідно до потреб та рівня практикуючого.', 'yoga2.png', 'Віньяса-йога', '120 грн.', 'Плинне Рухання та Гармонія'),
       ('Аштанга-йога - це одна з найстаріших і найпопулярніших систем йоги, що базується на древніх текстах та вченнях йоги. Цей термін складається з двох санскритських слів: "асhta" (що означає "вісім") та "анга" (що перекладається як "частка" або "етап"). Отже, "Аштанга-йога" перекладається як "восьмиступенева йога". Основна ідея Аштанга-йоги полягає в тому, щоб досягти гармонії між тілом, розумом та душею через практику асани (пози) та пранаями (контроль дихання). Ця система включає в себе восьмиступеневий шлях, який сприяє розвитку фізичного здоров''я, ментальної ясності та духовного зростання','yoga3.png','Аштанга-йога','150 грн.','Заглиблення у Силу та <b>Плинність</b>')
;