DROP TABLE IF EXISTS "PUBLIC"."SCHEDULE_RECORD";
CREATE
CACHED TABLE "PUBLIC"."SCHEDULE_RECORD" (
    "QUANTITY" INTEGER,
    "STATUS" CHARACTER VARYING(15),
    "DATE" DATE,
    "TIME" TIME(6),
    "ID" BIGINT NOT NULL,
    "SERVICE" CHARACTER VARYING(200)


);
ALTER TABLE "PUBLIC"."SCHEDULE_RECORD"
    ADD CONSTRAINT "PUBLIC"."CONSTRAINT_45" PRIMARY KEY ("ID");
