DROP TABLE IF EXISTS "PUBLIC"."COACH";
CREATE
CACHED TABLE "PUBLIC"."COACH" (
    "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1 RESTART WITH 2) NOT NULL,
    "PHONE_NUMBER" CHARACTER VARYING(20),
    "FULL_NAME" CHARACTER VARYING(70),
    "IMG_NAME" CHARACTER VARYING(20),
    "INFO" CHARACTER VARYING(450)
);
ALTER TABLE "PUBLIC"."COACH"
    ADD CONSTRAINT "PUBLIC"."CONSTRAINT_Coach1" PRIMARY KEY ("ID");
