DROP TABLE IF EXISTS "PUBLIC"."SCHEDULE_ITEM";
CREATE CACHED TABLE "PUBLIC"."SCHEDULE_ITEM"(
    "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1 RESTART WITH 5) NOT NULL,
    "QUANTITY" INTEGER,
    "SERVICE" BIGINT,
    "TIME" TIME,
    "SCHEDULE_RECORD_ID" BIGINT
);


DROP TABLE IF EXISTS "PUBLIC"."SCHEDULE_RECORD";
CREATE CACHED TABLE "PUBLIC"."SCHEDULE_RECORD"(
    "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1 RESTART WITH 2) NOT NULL,
    "DATE" DATE,
    "COACH_ID" BIGINT
);
